import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Alert } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import '/Users/luka/webgifaby/src/Ladding.css'
import firebase from 'firebase'

export class Login extends Component {

    constructor(props) {
        super(props);
        this.login = this.login.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
          email: '',
          password: ''
        };
      }

      handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
      }

      login(e) {
        e.preventDefault();
        firebase.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u)=>{
        }).catch((error) => {
            console.log(error);
          });
      }


    render() {
        return (
            <>
            <Form className='login' >
                        <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <input value={this.state.email} onChange={this.handleChange} id='emailAddress' type="email" placeholder="Enter email" />
                        <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                        </Form.Text>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <input value={this.state.password} onChange={this.handleChange} id='password' type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox" className='check'>
                        <Form.Check type="checkbox" label="Remeber me." />
                        </Form.Group>
                        <Button type="submit" onClick={this.login} variant='info' className='ogb' >
                        Login
                        </Button>

                        <Form.Group class='frg'>

                        </Form.Group>
                        </Form>
            </>
        )
}
}
export default Login
