import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Alert } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import '/Users/luka/webgifaby/src/Ladding.css'
import firebase from 'firebase'

export class Login extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',

        }
        this.onSignUp = this.onSignUp.bind(this)
    }

    onSignUp(){
        const { email, password } = this.state
        firebase.auth().signInWithEmailAndPassword(email, password)
        .then((result) => {
            console.log(result)
        })
        .catch((error) => {
            console.log(error)
        })
    }
    handleChange(event) {
        this.setState({
            email: event.target.email,
            password: event.target.password
        })
    }

    render() {
        return (
            <>
            <Form className='login'>
                        <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control onChange={(email) => this.setState({email})} value={this.state.email} name='email' type="email" placeholder="Enter email" />
                        <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                        </Form.Text>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control onChange={(password) => this.setState({password})} value={this.state.password} name='password' type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox" className='check'>
                        <Form.Check type="checkbox" label="Remeber me." />
                        </Form.Group>
                        <Button onClick={() => this.onSignUp()} variant='info' className='ogb' >
                        Login
                        </Button>

                        <Form.Group class='frg'>

                        </Form.Group>
                        </Form>
            </>
        )
}
}
export default Login
