import React, { Component } from 'react'
import firebase from 'firebase'
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Alert } from 'react-bootstrap';
import './Register.css'



export default class frgp extends Component {


    constructor(props) {
        super(props);

        this.state = {
            email: '',
        }
        this.handleChange = this.handleChange.bind(this)
        this.onSignUp = this.onSignUp.bind(this)
    }

    onSignUp(){
        const { email } = this.state
        firebase.auth().sendPasswordResetEmail(email).then(function() {
            // Email sent.
          }).catch(function(error) {
            console.log(error)
          });
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }


    render() {
        return (
            <div className='all' >
                <Form className='con' onSubmit={this.onSignUp} >
                    <h2>Forgot Password</h2>
                    <Form.Group controlId="exampleForm.ControlInput1">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control onChange={this.handleChange} name='email' type="email" placeholder="name@example.com" />
                        <Button className='bb' variant='info' onClick={this.onSignUp}>Send</Button>
                    </Form.Group>
                </Form>
            </div>
        )
    }
}
