import React, { Component, useState, setState } from 'react'
import firebase from 'firebase'
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Alert } from 'react-bootstrap';
import './Register.css'
import ReactDOM from 'react-dom';



export default function Frgp() {





    const [ params, setParams ] = useState({
        email: '',
    })


    onSignUp = (e) => {
        const { email } = this.state
        firebase.auth().sendPasswordResetEmail(email).then(function() {

          }).catch(function(error) {
            console.log(error)
          });
        setTimeout(() => {
            window.location.href = '/'
        }, 4000)
    }

    handleChange = (e) => {
        setParams({...params, [e.target.name]: e.target.value  })
    }



        return (
            <div className='all' >
                <div className='anso' ></div>
                <Form className='con' onSubmit={onSignUp} >
                    <center><h2>Forgot Password</h2></center>
                    <Form.Group className='bbn' controlId="exampleForm.ControlInput1">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control onChange={handleChange} name='email' type="email" placeholder="name@example.com" />
                        <Button className='bb' variant='info' onClick={onSignUp}>Send</Button>
                    </Form.Group>
                </Form>
            </div>
        )
    }
