import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Alert } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import firebase from 'firebase'
import '/Users/luka/webgifaby/src/Ladding.css'
import React, { useEffect, useRef, Component} from 'react';
import lottie from 'lottie-web';



export class Login extends Component {



    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',

        }
        this.onSignUp = this.onSignUp.bind(this)
    }

    onSignUp(){
        const { email, password } = this.state
        firebase.auth().signInWithEmailAndPassword(email, password)
        .then((result) => {
            console.log(result)
        })
        .catch((error) => {
            console.log(error)
        })
    }



    render() {
        return (
            <>
            <div className='all'>  
                    <div className='logo'></div>
                    <div className='navbar'>

                    </div>
                    <div>
                        <div className='welcome'>

                        </div>
                        <div className='anim'  >
                        </div>
                        <div className='Form'>
                            <Form className='login'>
                            <Form.Group controlId="formBasicEmail">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control onChange={(email) => this.setState({email})} id='emailAddress' type="email" placeholder="Enter email" />
                            <Form.Text className="text-muted">
                            We'll never share your email with anyone else.
                            </Form.Text>
                            </Form.Group>
                            <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control onChange={(password) => this.setState({password})} id='password' type="password" placeholder="Password" />
                            </Form.Group>
                            <Form.Group controlId="formBasicCheckbox" className='check'>
                            <Form.Check type="checkbox" label="Remeber me." />
                            </Form.Group>
                            <Button onClick={() => this.onSignUp()} variant='info' className='ogb' >
                            Login
                            </Button>

                            <Form.Group class='frg'>

                            </Form.Group>
                            </Form>
                        </div>
                        <div className='line' ><h8>Or</h8></div>
                        <div className='registerf' > 
                            <Button variant="outline-info" href="Register" className='gilaki'>Register</Button>
                        </div>
                    </div>   
                    </div> 

            </>
    )
}
}


export default Login
