import '/Users/luka/webgifaby/src/Register.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react'
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import { FaGoogle, FaFacebookF, FaApple } from 'react-icons/fa';
import { AiOutlineArrowLeft } from 'react-icons/ai'
import firebase from 'firebase'


export class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            firstname: '',
            lastname: ''
        }
        this.onSignUp = this.onSignUp.bind(this)
    }

    onSignUp(){
        const { email, password, lastname, firstname } = this.state
        firebase.auth().createUserWithEmailAndPassword(email, password)
            .then((result) =>{
                firebase.firestore().collection("users")
                    .doc(firebase.auth().currentUser.uid)
                    .set({
                        firstname,
                        lastname,
                        email,
                        password
                    })
                console.log(result)
                if(result===undefined){
                    return(
                        <div>

                        </div>
                    )
                }
        })
        .catch((error) => {
            console.log(error.message)
        })
    }
 render() {
    return(
        <>
        <div className='all'>
            <a href="/" ><AiOutlineArrowLeft className='back' /></a>
            <title>Gifaby | Register</title>
            <div className='logoo' ></div>
            <div>
                <Form className='loginn'>
                    <Form.Group>
                    <Form.Label>First Name</Form.Label>
                        <Form.Control type="name" placeholder="Enter First Name" />
                    </Form.Group>
                    <Form.Group>
                    <Form.Label>Last Name</Form.Label>
                        <Form.Control type="name" placeholder="Enter Last Name" />
                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" />
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox" className='checkk'>
                            <Form.Check type="checkbox" label="You agree to our terms , data management policy and cookie usage policy ." />
                        </Form.Group>
                        <Button className='loginbtnn' variant="info" type="submit">
                            SignUp
                        </Button>
                        <Form.Group class='frg'>

                        </Form.Group>
                    </Form>
                    <div className='linee' ></div>
                    <div>
                    <Button className='apple' variant="info"><FaApple  className='icona'/>   Continue as apple</Button>
                        <Button className='facebook' variant="info"><FaFacebookF  className='iconf'/>   Continue as Facebook</Button>
                        <Button className='google' variant="info"><FaGoogle  className='icon'/>   Continue as Google</Button>
                    </div>

            </div>
        </div>
        </>
    )
}
}
export default Register
