import React, {
    useEffect,
    useState
} from 'react'
import { render } from '@testing-library/react'
import './gifaby.css'
import { user } from './redux/reducers/user'
import { Helmet } from 'react-helmet'
import newuser from './newuser'
import { Modal, Button } from 'react-bootstrap'
import Feed from './Feed'
import Avatar from '@material-ui/core/Avatar'
import firebase from 'firebase'



function Home() {




    const [posts, setPosts] = useState([]);

    useEffect(() => {
        firebase.firestore().collection('posts').onSnapshot(snapshot => {
            setPosts(snapshot.docs.map(doc => ({
                id: doc.id,
                post: doc.data()
            })))
        })
    }, [])

    return (
        <>
            <div className='allbg' >
            <Helmet>
              <title>Gifaby | Home</title>
            </Helmet>
            <nav>
                <div className='navbarh' ></div>
                <div className='logoh'></div>
            </nav>
                <div className='Feed' >
                    {
                        posts.map(({id, post}) => (
                            <Feed key={id} username={post.username} description={post.description} imageUrl={post.imageUrl} />
                        ))
                    }
                </div>

            </div>
        </>


    )
}

export default Home
