import React, { Component, Link } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Alert, Spinner } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import '/Users/luka/webgifaby/src/Ladding.css'
import firebase from 'firebase'

export class Login extends Component {





    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            loading: false,

        }
        this.onSignUp = this.onSignUp.bind(this)
    }


    onSignUp(event) {
        const { email, password } = this.state
        firebase.auth().signInWithEmailAndPassword(email, password)
        .then((result) => {
            console.log(result)
        })
        .catch((error) => {
            console.log(error)
        })
        this.setState({ loading : true })

        setTimeout(()=>{
            this.setState({ loading : false })
        }, 3000)

    }
    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        const {loading} = this.state

        return (
            <>
            <Form className='login' onSubmit={this.onSignUp} >
                        <Form.Group controlId="formBasicEmail">
                        <Form.Label id='liso' >Email address</Form.Label>
                        <Form.Control onChange={this.handleChange} name='email' type="email" placeholder="Enter email" />
                        <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                        </Form.Text>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                        <Form.Label id='liso' >Password</Form.Label>
                        <Form.Control onChange={this.handleChange} name='password' type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox" className='check'>
                        <Form.Check type="checkbox" label="Remeber me." />
                        </Form.Group>
                        <Button disabled={loading} id='lico' onClick={this.onSignUp} variant='info' className='ogb' >
                        { loading && <Spinner animation="grow" variant="light" /> }
                        { loading && <span>Loading...</span> }
                        { !loading && <span>Login</span> }
                        </Button>
                        <Button type='submit' className='linkb' href='/reset' variant="link">Forgot Password?</Button>
                        <Form.Group class='frg'>

                        </Form.Group>
                        </Form>
            </>
        )
}
}
export default Login
