import '/Users/luka/webgifaby/src/Register.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react'
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import { FaGoogle, FaFacebookF, FaApple } from 'react-icons/fa';
import { AiOutlineArrowLeft } from 'react-icons/ai'
import firebase from 'firebase'
import { Helmet } from 'react-helmet'

var provider = new firebase.auth.FacebookAuthProvider();

export class Register extends Component {



    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            firstname: '',
            lastname: ''
        }
        this.onSignUp = this.onSignUp.bind(this)
    }

    onFace() {
        firebase.auth().signInWithPopup(provider).then(function(result) {
            // This gives you a Facebook Access Token. You can use it to access the Facebook API.
            var token = result.credential.accessToken;
            // The signed-in user info.
            var user = result.user;
            // ...
          }).catch(function(error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;
            // The email of the user's account used.
            var email = error.email;
            // The firebase.auth.AuthCredential type that was used.
            var credential = error.credential;
            // ...
          });
    }

    onSignUp(){
        const { email, password, lastname, firstname } = this.state
        firebase.auth().createUserWithEmailAndPassword(this.state.email, this.state.password)
            .then((result) =>{
                firebase.firestore().collection("users")
                    .doc(firebase.auth().currentUser.uid)
                    .set({
                        firstname,
                        lastname,
                        email,
                        password
                    })
                console.log(result)
                if(result===undefined){
                    return(
                        <div>

                        </div>
                    )
                }
        })
        .catch((error) => {
            console.log(error.message)
        })
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

 render() {
    return(
        <>
        <div className='all'>
        <Helmet>
              <title>Gifaby | Register</title>
            </Helmet>
            <a id='liso' href="/" ><AiOutlineArrowLeft className='back' /></a>
            <title>Gifaby | Register</title>
            <div className='logoo' ></div>
            <div className='nr1' ></div>
            <div className='nr2'></div>
            <div className='nr3'></div>
            <div>
                <Form onSubmit={this.onSignUp} className='loginn'>
                    <Form.Group>
                    <Form.Label id='liso' >First Name</Form.Label>
                        <Form.Control onChange={this.handleChange} name='firstname' type="name" placeholder="Enter First Name" />
                    </Form.Group>
                    <Form.Group>
                    <Form.Label id='liso'>Last Name</Form.Label>
                        <Form.Control onChange={this.handleChange} name='lastname' type="name" placeholder="Enter Last Name" />
                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label id='liso'>Email address</Form.Label>
                        <Form.Control onChange={this.handleChange} name='email' type="email" placeholder="Enter email" />
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                            <Form.Label id='liso'>Password</Form.Label>
                            <Form.Control onChange={this.handleChange} name='password' type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox" className='checkk'>
                            <Form.Check type="checkbox" label="You agree to our terms , data management policy and cookie usage policy ." />
                        </Form.Group>
                        <Button id='lico' onClick={this.onSignUp} className='loginbtnn'  >
                            SignUp
                        </Button>
                        <Form.Group class='frg'>

                        </Form.Group>
                    </Form>
                    <div className='linee' ></div>
                    <div>
                    <Button id='lico' className='apple'><FaApple  className='icona'/>   Continue as apple</Button>
                        <Button id='lico' onClick={this.onFace} className='facebook' ><FaFacebookF  className='iconf'/>   Continue as Facebook</Button>
                        <Button id='lico' className='google' ><FaGoogle  className='icon'/>   Continue as Google</Button>
                    </div>

            </div>
        </div>
        </>
    )
}
}
export default Register
